import React, { Component } from "react";
class Winner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName1: "jayeshkattar",
            userName2: "HarshithGudapati",
            message1: "",
            message2: "",
        };
    }
    getUserName1 = (e) => {
        this.setState({
            userName1: e.target.value,
        });
    };
    getUserName2 = (e) => {
        this.setState({
            userName2: e.target.value,
        });
    };
    validate1 = () => {
        fetch(`https://api.github.com/users/${this.state.userName1}/followers`)
            .then((response) => response.json())
            .then((data) => {
                var result1 = (data.length);
                this.setState({
                    message1: result1,
                })
                // console.log(result1);
                return result1;

            })
            .catch((error) => error);

    }
    validate2 = () => {
        this.validate1();
        fetch(`https://api.github.com/users/${this.state.userName2}/followers`)
            .then((response) => response.json())
            .then((data) => {
                var result2 = (data.length);
                this.setState({
                    message2: result2,
                })
                // console.log(result2);

                return result2;
            })
            .catch((error) => error);
    }
    validate = () => {
        const a = this.validate1();
        const b = this.validate2();
        console.log(a);
        console.log(this.validate1);
        if (a>b) {
            console.log("a is greater");
        }
        else {
            console.log("b is greater");
        }
    }
    render() {
        return (
            <div className="container" >
                <input type="text" id="userName1" style={{ margin: "10px" }} onChange={this.getUserName1}  /><br />
                <input type="text" id="userName2" style={{ margin: "10px" }} onChange={this.getUserName2} />
                <button onClick={this.validate2}>Validate</button>
                <p>{this.state.message1}</p>
                <p>{this.state.message2}</p>
            </div>
        );
    }
}
export default Winner;
