import React, { Component } from "react";
class Winner extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName1: "",
            userName2: "",
            message1: "",
            message2: "",
        };
    }
    getUserName1 = (e) => {
        this.setState({
            userName1: e.target.value,
        });
    };
    getUserName2 = (e) => {
        this.setState({
            userName2: e.target.value,
        });
    };

    validate1 = () => {
        fetch(`https://api.github.com/users/${this.state.userName1}/followers`)
            .then((response) => response.json())
            .then((data) => {
            var result = (data.length);

            this.setState({
                           message1: result,
                        })


             })

            .catch((error) => error);

    }
    validate2 = () => {
        this.validate1();

        fetch(`https://api.github.com/users/${this.state.userName2}/followers`)
            .then((response) => response.json())
            .then((data) => {
            var result = (data.length);
            this.setState({
                           message2: result,
                        })
                        return result;

             })

            .catch((error) => error);

    }
    validate = () =>{
        var a = this.validate1;
        var b = this.validate2;
        if(a>b){
            console.log("a is greater");
        }
        else{
            console.log("b is greater");
        }

    }
    render() {
        return (
            <div className="container" >
                <input type="text" id="userName1" style={{ margin: "10px" }} onChange={this.getUserName1} /><br />
                <input type="text" id="userName2" style={{ margin: "10px" }} onChange={this.getUserName2} />
                <button onClick={this.validate}>Validate</button>
        <p>{this.state.message1}</p>
        <p>{this.state.message2}</p>

            </div>
        );
    }
}
export default Winner;
