import React, { Component } from "react";
class Followers extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userName1: "",
            userName2: "",
            message: "dwdw",
        };
    }
    getUserName1 = (e) => {
        this.setState({
            userName1: e.target.value,
        });
    };
    getUserName2 = (e) => {
        this.setState({
            userName1: e.target.value,
        });
    };
    validate = () => {
        const username = document.getElementById('username').value;
        const username2 = document.getElementById('username2').value;
        fetch(`https://api.github.com/users/${this.state.userName1}/followers`)
            .then((response) => response.json())
            .then((data) => {
                data.filter((item) => item.login === this.state.userName2).length > 0
                    ? this.setState({
                        message: `${this.state.userName1} is following ${this.state.userName2}`,
                    })
                    : this.setState({
                        message: `${this.state.userName1} is not following ${this.state.userName2}`,
                    })
            })
            .catch((error) => error);
    }
    render() {
        return (
            <div className="container" >
                <input type="text" id="userName1" style={{ margin: "10px" }} onChange={this.state.getUserName1} /><br />
                <input type="text" id="userName2" style={{ margin: "10px" }} onChange={this.state.getUserName2} />
                <button onClick={this.state.validate}>Validate</button>
            </div>
        );
    }
}
export default Followers;
